ObtenerUI.sql:
        
			DECLARE @XmlData XML;
			SET @XmlData = (
				SELECT UnidadUI.indice, UnidadUI.orden, tipoUnidadUI.descripcion,tipoUnidadUI.indice tipoUnidad, tipoUnidadUI.innerHTML,tipoUnidadUI.css,tipoUnidadUI.javascript, propiedad.descripcion, valor 
				FROM META_UnidadesUI unidadUI 
				INNER JOIN [META_TiposUnidadUI] tipoUnidadUI ON unidadUI.tipoUnidadUI=tipoUnidadUI.indice
				LEFT OUTER JOIN META_PropiedadesTiposUnidadesUI propiedad ON tipoUnidadUI.indice=propiedad.tipoUnidadUI
				LEFT OUTER JOIN META_ValoresPropiedadesUnidadesUI valor ON valor.propiedad=propiedad.indice
				ORDER BY unidadUI.orden ASC
				FOR XML AUTO, ROOT('ui')
			);
			SELECT @XmlData AS xmldoc;

ObtenerEstructuraTable.sql:

	SELECT column_name campo, data_type tipo, ISNULL(CHARACTER_MAXIMUM_LENGTH,0) longitud,CONVERT(BIT,CASE WHEN IS_NULLABLE='YES' THEN 0 ELSE 1 END) requerido 
	FROM INFORMATION_SCHEMA.COLUMNS 
	WHERE TABLE_NAME=@sobject;
